            videoHeight = videoWidth/1.7

        } else
        {
                videoWidth = 300
                videoHeight = 100
        }

        this.setState((preS) => {
            return {
            expanded: !preS.expanded,
            playing: !preS.playing,
            height: videoHeight,
            width: videoWidth
            }
        })
    }

    componentDidUpdate()
    {
        this.state.playing ? video.play() : video.pause()
    }

    render()
    {
        return(
        <video height={this.state.height} width={this.state.width} onClick={(e) => this.handleThumbnailClicked(e)}>
            <source src={this.props.src} type="video/mp4" />
        </video>
        )
    }
}

export default withTheme()(VideoThumb)
