import React from 'react'
import './react-video-gallery.css'


class VideoGallery extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            shownCategory: null,
            children: this.props.children
        }

        this.handleAccordionClick = this.handleAccordionClick.bind(this)
    }

    handleAccordionClick(tag)
    {
        this.props.children = React.Children.map(this.props.children, child => {
            React.cloneElement(child, {collapsed: true})
        })
    }

    render()
    { 
        let proppedChildren = React.Children.map(this.state.children, child => {
            return React.cloneElement(child, {handleAccordionClick: this.handleAccordionClick})
        })

        return(
        <div id="gallery" >
            {proppedChildren}
        </div>
        )
    }
}

