import React from 'react'
import { withTheme } from '@material-ui/core/styles'
import VideoThumbnail from 'react-video-thumbnail'

function ExpandedVideo(props)
{
    return(
        <div id="fullscreen-modal">
            <video id="expanded-video" autoPlay="autoplay">
                <source src={props.src} type="video/mp4" />
            </video>
        </div>
    )
}

class VideoThumb extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            expanded: false
        }
        this.handleThumbnailClicked = this.handleThumbnailClicked.bind(this)
    }
    handleThumbnailClicked()
    {
        this.setState({
            expanded: true
        })
    }

    render()
    {
        return(
            <VideoThumbnail
                videoUrl={this.props.url}
                thumbnailHandler={() => this.handleThumbnailClicked}
                width={120}
                height={80}
                /> 
            )
    }
}

export default withTheme()(VideoThumb)
