import React from 'react'
import { withTheme } from '@material-ui/core/styles'

class VideoThumb extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            expanded: false
        }
        this.handleThumbnailClicked = this.handleThumbnailClicked.bind(this)
    }

    getScreenDimensions()
    {
        let sh = window.innerHeight
        let sw = window.innerWidth
        return {height: sh, width: sw}
    }

    handleThumbnailClicked(e)
    {
        const video = e.currentTarget
        const viewportDimensions = this.getScreenDimensions


        let videoWidth = viewportDimensions["width"]
        let videoHeight = video
    }

    render()
    {
        return(
        <video height={100} width={300} onClick={(e) => this.handleThumbnailClicked(e)}>
            <source src={this.props.src} type="video/mp4" />
        </video>
        )
    }
}

export default withTheme()(VideoThumb)
