import React from 'react'
import './react-video-gallery.css'
import {VideoCategoryCard} from './react-video-category-card'


class VideoGallery extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            shownCategory: "Events"
        }

        this.handleAccordionClick = this.handleAccordionClick.bind(this)
    }

    handleAccordionClick(categoryName)
    {
        this.setState({
            shownCategory: categoryName
        })
    }

    render()
    { 

        return(
        <div id="gallery" >

            <VideoCategoryCard
                category="Events"
                url="https://www.youtube.com/embed/C0RFDUYM1xQ"
                handleAccordionClick = {this.handleAccordionClick}
                collapsed = {this.state.shownCategory === "Events" ? false : true} />
            <VideoCategoryCard
                category="Speeches"
                url="https://www.youtube.com/embed/C0RFDUYM1xQ"
                handleAccordionClick = {this.handleAccordionClick}
                collapsed = {this.state.shownCategory === "Speeches" ? false : true} />
            <VideoCategoryCard
                category="Tours"
                url="https://www.youtube.com/embed/C0RFDUYM1xQ"
                handleAccordionClick = {this.handleAccordionClick}
                collapsed = {this.state.shownCategory === "Tours" ? false : true} />
        </div>
        )
    }
}

export {VideoGallery}
