import React from 'react'
import './react-video-gallery.css'


class VideoGallery extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            shownCategory: "Events"
        }

        this.handleAccordionClick = this.handleAccordionClick.bind(this)
    }

    handleAccordionClick(categoryName)
    {
        this.setState({
            shownCategory: categoryName
        })

        this.forceUpdate
    }

    componentDidUpdate()
    {
        console.log("updated")
    }

    render()
    { 
        let proppedChildren = React.Children.map(this.props.children, child => {
            return React.cloneElement(child, {
                                                handleAccordionClick: this.handleAccordionClick,
                                                collapsed: child.category === this.state.shownCategory ? false : true
                                            })
        })

        return(
        <div id="gallery" >
            {proppedChildren}
        </div>
        )
    }
}

export {VideoGallery}
