import React, { Fragment } from "react";
import { default as Banner } from "../assets/Office-Door-1Small copy.jpg";

const textContainerStyle = {
	display: "flex",
	justifyContent: "space-between"
};

const titleStyle = {
	marginRight: "3em"
};

const descriptionStyle = {
	width: "40vw",
	paddingRight: "14em"
};
export default class AboutUsPanel extends React.Component {
	componentDidMount() {
		const me = document.getElementById("aboutusMarker");
		let myPosition = me.getBoundingClientRect().top + window.scrollY;
		this.props.addSectionTrigger("aboutusMarker", myPosition);
	}
	render() {
		return (
			<Fragment>
				<div id="aboutusMarker" />
				<img src={Banner} alt="Office Banner" />
				<div className="textContainer" style={textContainerStyle}>
					<h1 style={titleStyle}>Shed Studio is...</h1>
					<p style={descriptionStyle}>{this.props.description}</p>
				</div>
			</Fragment>
		);
	}
}
