import React from 'react'
import './react-video-category-card.css'

class VideoCategoryCard extends React.Component
{
    constructor(props)
    {
        super(props)
    }
    render()
    {
        return(
            <div className="video-category-card"
                onClick={() => this.props.handleAccordionClick(this.props.category)}>
                <h1 className="category-tag">{this.props.category}</h1>
                <iframe
                title={this.props.category} 
                width="560" 
                height="315" 
                src={this.props.url + "?rel=0&modestbranding=1&autohide=1&showinfo=0&controls=0"} 
                frameBorder="0" 
                allow="accelerometer; encrypted-media; gyroscope; picture-in-picture" 
                allowFullScreen>
                </iframe>
            </div>
        )
    }
}

export {VideoCategoryCard}
