import React from 'react'

export default class VideoThumb extends React.Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            expanded: false,
            playing: false,
            height: 100,
            width: 300
        }
        this.handleThumbnailClicked = this.handleThumbnailClicked.bind(this)
    }

    getScreenDimensions()
    {
        let sh = window.innerHeight
        let sw = window.innerWidth
        return {height: sh, width: sw}
    }

    handleThumbnailClicked(e)
    {
        let videoHeight
        let videoWidth
        let style
        const video = e.currentTarget

        if(!this.state.expanded)
        {
            const viewportDimensions = this.getScreenDimensions()


            videoWidth = viewportDimensions["width"]
            videoHeight = videoWidth/1.7


            style = {position: "absolute",
                    zIndex: "1000",
                    top: 0}
        } else
        {
                videoWidth = 300
                videoHeight = 100

                style = {}
        }

        this.setState((preS) => {
            return {
            expanded: !preS.expanded,
            playing: !preS.playing,
            style: style,
            height: videoHeight,
            width: videoWidth
            }
        }, () => {
            this.state.playing ? video.play() : video.pause()
        })
    }

    render()
    {
        return(
        <video
        className="video-thumbnail" 
        height={this.state.height} 
        width={this.state.width} 
        style={this.state.style}
        onClick={(e) => this.handleThumbnailClicked(e)}>
            <source src={this.props.src} type="video/mp4" />
        </video>
        )
    }
}
