import React from "react";

var linkStyle = {
	fontSize: "1.5em",
	textDecoration: "none",
	marginLeft: "auto"
};

const sideBarItemStyle = {
	display: "flex",
	flexDirection: "column",
	width: "8em"
};

export default class SideBarItem extends React.Component {
	constructor(props) {
		super(props);
		this.convertedTitle = this.convertTitle(props.title);
		this.state = {
			color: "white",
			scaleUp: false
		};

		this.determineStyle = this.determineStyle.bind(this);
		this.handleScroll = this.handleScroll.bind(this);
	}

	convertTitle(title) {
		return title.replace(" ", "");
	}

	determineStyle() {
		if (this.state.scaleUp) {
			return {
				width: "3.5em",
				height: ".5em"
			};
		} else {
			return {
				width: "1.75em",
				height: ".15em"
			};
		}
	}

	handleScroll() {
		const me = document.getElementById(this.convertedTitle);
		const bottom = me.getBoundingClientRect().bottom + window.scrollY;

		if (bottom > this.props.threshold) {
			this.setState(oldState => {
				if (oldState.color === "white") {
					return { color: "black" };
				}
			});
		} else {
			this.setState(oldState => {
				if (oldState.color === "black") {
					return { color: "white" };
				}
			});
		}
	}

	componentDidMount() {
		window.addEventListener("scroll", this.handleScroll);
	}

	componentWillUpdate() {
		this.setState({
			scaleUp: true
		});
	}

	render() {
		return (
			<div
				className="sideBarItem"
				id={this.convertedTitle}
				style={sideBarItemStyle}
			>
				<a
					className="sideBarItem-title"
					href={`#${this.convertedTitle}Marker`}
					style={{ ...linkStyle, color: this.state.color }}
				>
					{this.props.title}
				</a>
				<svg
					style={{
						...this.determineStyle(),
						marginLeft: "auto",
						marginTop: "1em",
						marginBottom: "1em"
					}}
				>
					<rect style={this.determineStyle()} fill={this.state.color} />
					Sorry, your browser does not support inline SVG.
				</svg>
			</div>
		);
	}
}
