import React, { Fragment } from 'react'
import './contactDropDown.css'

export default class ContactDropDown extends React.Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            expanded: false
        }

        this.handleClick = this.handleClick.bind(this)
    }

    handleClick()
    {

        this.setState(preS => {
            let expanded = !preS.expanded
            return {expanded: expanded}
        })
    }

    render()
    {
        return(
            <Fragment>
                <h1 id="drop-down-trigger" onClick={() => this.handleClick()}>Contact Shed</h1>


                    {}
                    <div id="contact-drop-down">
                        <a href={"mailto:" + this.props.email} id="email-link">{this.props.email}</a>
                        <ol id="social-container">
                            {
                                Object.keys(this.props.social).map(site => {
                                    return (
                                        <li key={site} className="social-link"><a href={this.props.social[site]}>{site}</a></li>
                                    )
                                })
                            }
                        </ol>
                    </div>
                    }
            </Fragment>

        )
    }

}
